inline float32 Sin(float32 fRad, bool useTable = true)
{
	if (!useTable)
		return (float32)sin(fRad);

	int32 index = 0;
	if (fRad > 0)
	{
		index = (int32)(fRad * (TRIG_TABLE_SIZE / TWO_PI)) % TRIG_TABLE_SIZE;
	}
	else
	{
		index = TRIG_TABLE_SIZE -
			((int32)(-fRad * (TRIG_TABLE_SIZE / TWO_PI)) % TRIG_TABLE_SIZE) - 1;
	}

	return CosSinTable[index];
}

inline float32 Cos(float32 fRad, bool useTable = true)
{
	if (!useTable)
		return (float32)cos(fRad);

	float32 cValue = Sin((float32)(fRad + HALF_PI));

	return cValue;
}

inline float32 Tan(float32 fRad, bool useTable = true)
{
	if (!useTable)
		return (float32)tan(fRad);

	int index = (int32)(fRad * (TRIG_TABLE_SIZE / TWO_PI)) % TRIG_TABLE_SIZE;
	return TanTable[index];
}

