inline void UIntToBinary(uint32 value, vector<int>& t)
{
	int32 temp = value % 2;
	value = value >> 1;
	if (value > 0)
	{
		UIntToBinary(value, t);
	}
	t.push_back(temp);
}


// ×î½üµÄ¶þ´ÎÃÝ
inline int32 NearestPowerOfTwo(uint32 value)
{
	vector<int> t;
	UIntToBinary(value, t);
	int highestBit = t.size();

	if (value == (1 << (highestBit - 1)))
	{
		highestBit--;
	}

	return highestBit;
}


